mapper.update:

UPDATE dms_tb_cart t
   SET t.modify_date = NOW(),
	   t.modify_user = #{modifyUser},
	   t.quantity = t.quantity + #{quantity}
 WHERE t.state = 'U'
   AND t.user_id = #{userId}
   AND t.shop_id = #{shopId}
   AND t.sup_id  = #{supId}
   AND t.item_id = #{itemId}
   AND t.sku_id  = #{skuId}

mapper.select:

SELECT COUNT(1)
  FROM dms_tb_cart t
 WHERE t.STATE = 'U'
   AND t.user_id = #{userId}
   AND t.shop_id = #{shopId}

SELECT t.cart_id AS cartId, t.sup_id AS supId, t.item_id AS itemId, t.sku_id AS skuId, t.quantity FROM dms_tb_cart t WHERE t.state = 'U' AND t.user_id = #{userId} AND t.shop_id = #{shopId}
AND t.cart_id IN #{code}
ORDER BY t.sup_id, t.create_date DESC

